			     (
					(
						COMP_CODE = #{compCode} 
						AND  BRANCH_CODE = #{branchCode}
					)
					OR
					(
						COMP_CODE = 0 
						AND  BRANCH_CODE = 0 
					) 	
				 )
                AND
                EXISTS (SELECT 1 FROM OPT WHERE OPT.PROG_REF = AXS.PROG_REF AND OPT.APP_NAME = #{appName})
			   )

