mapper.select:
SELECT TOP 1 
	   APP_NAME
      ,USER_ID
      ,PROG_REF
      ,OBJECT_ID
      ,USER_PREFERENCES
  FROM SYS_PARAM_USER_PREFERENCES 
 WHERE (APP_NAME = #{APP_NAME}
   AND USER_ID = #{USER_ID}
   AND PROG_REF = #{PROG_REF}
   AND OBJECT_ID = #{OBJECT_ID}
   AND IS_DEFAULT = 'N'
   AND UPPER(OBJECT_TYPE) = UPPER(#{OBJECT_TYPE}))
   OR
   (APP_NAME = #{APP_NAME}
   AND PROG_REF = #{PROG_REF}
   AND OBJECT_ID = #{OBJECT_ID}
   AND IS_DEFAULT = 'Y'
   AND UPPER(OBJECT_TYPE) = UPPER(#{OBJECT_TYPE})
   AND NOT EXISTS (SELECT 1
  FROM SYS_PARAM_USER_PREFERENCES 
 WHERE APP_NAME = #{APP_NAME}
   AND USER_ID = #{USER_ID}
   AND PROG_REF = #{PROG_REF}
   AND OBJECT_ID = #{OBJECT_ID}
   AND IS_DEFAULT = 'N'
   AND UPPER(OBJECT_TYPE) = UPPER(#{OBJECT_TYPE})))
   
   AND ROWNUM=1

