AND (CIF.STATUS = 'A')
AND CIF.STATUS IN ('I', 'A')
AND ((CASE WHEN #{raType} IS NULL OR #{raType} = '' THEN 'X' ELSE #{raType} END = 'X') OR EXISTS (SELECT 1 FROM RIFCTT WHERE CIF.COMP_CODE = RIFCTT.COMP_CODE AND CIF.CIF_TYPE = RIFCTT.TYPE_CODE AND ((CASE WHEN #{raType} IS NULL OR #{raType} = '' THEN 'X' ELSE #{raType} END = 'B' AND RIFCTT.BANK = 'Y') OR (CASE WHEN #{raType} IS NULL OR #{raType} = '' THEN 'X' ELSE #{raType} END = 'C' AND RIFCTT.CLIENT = 'Y'))))
