AND AMF.GL_CODE = #{glCode}
AND AMF.ACC_BC = #{ACC_BC}
AND AMF.GMI_FLAG IN ('F','G')
AND AMF.GMI_FLAG = #{GMI_FLAG}
AND ((CASE WHEN #{affectPassChq} IS NULL OR #{affectPassChq} = '' THEN '-1' ELSE #{affectPassChq} END = '-1') OR EXISTS (SELECT 1 FROM RIFATT WHERE AMF.COMP_CODE = RIFATT.COMP_CODE AND AMF.GL_CODE = RIFATT.TYPE_CODE AND ((CASE WHEN #{affectPassChq} IS NULL OR #{affectPassChq} = '' THEN '-1' ELSE #{affectPassChq} END = '-1') OR (#{affectPassChq} = '0' AND RIFATT.AFFECTION = '0') OR (#{affectPassChq} IN ('1', '2') AND RIFATT.AFFECTION = #{affectPassChq} AND (CASE WHEN AMF.UPDATE_PASSBOOK IS NULL OR AMF.UPDATE_PASSBOOK = '' THEN '0' ELSE AMF.UPDATE_PASSBOOK END = '1')))))
AND ((#{AFFECT_CARD} <> '1') OR EXISTS (SELECT 1 FROM RIFATT WHERE AMF.COMP_CODE = RIFATT.COMP_CODE AND AMF.GL_CODE = RIFATT.TYPE_CODE and RIFATT.AFFECT_CARD = '1' AND #{AFFECT_CARD} = 'A') )
)
AND CIF.CIF_NO = #{cifCode}

	 
