mapper.update:
begin
DELETE CIF_MOSBLACKLIST_UPDATES_TEMP WHERE USER_ID = #{userId}
;
INSERT INTO CIF_MOSBLACKLIST_UPDATES_TEMP (USER_ID, COMP_CODE, CIF_NO, NEW_STATUS) select #{userId}, CIF.COMP_CODE, CIF.CIF_NO, '1' from CIF WHERE COMP_CODE = #{compCode} AND CIF_NO = #{cifCode} AND CIF.STATUS IN('A','S','C','I') AND CIF.CIF_NO != 0 AND CIF.CIF_NO IS NOT NULL
;
UPDATE CIF SET BLACK_LISTED = 1 WHERE COMP_CODE = #{compCode} AND CIF_NO = #{cifCode} AND CIF.STATUS IN('A','S','C','I') AND CIF.CIF_NO != 0 AND CIF.CIF_NO IS NOT NULL
;
INSERT INTO CIF_MOSBLACKLIST_UPDATES_TEMP (USER_ID, COMP_CODE, CIF_NO, NEW_STATUS) select #{userId}, CIF.COMP_CODE, CIF.CIF_NO, '0' from CIF WHERE COMP_CODE = #{compCode} AND BLACK_LISTED = 1 AND CIF.STATUS IN ('A', 'S', 'C', 'I') AND EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE = #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE M.COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM MOSBLACKLIST MOS WHERE MOS.COMP_CODE = #{compCode} AND MOS.CIF = CIF.CIF_NO AND MOS.STATUS = 'P' AND MOS.CODE != #{blackListCode})
;
UPDATE CIF SET BLACK_LISTED = 0 WHERE COMP_CODE = #{compCode} AND BLACK_LISTED = 1 AND CIF.STATUS IN ('A', 'S', 'C', 'I') AND EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE = #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE M.COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM MOSBLACKLIST MOS WHERE MOS.COMP_CODE = #{compCode} AND MOS.CIF = CIF.CIF_NO AND MOS.STATUS = 'P' AND MOS.CODE != #{blackListCode})
;
DELETE FROM CIF_MOSBLACKLIST WHERE COMP_CODE = #{compCode} AND MOS_CODE = #{blackListCode}
;
INSERT INTO CIF_MOSBLACKLIST_UPDATES_TEMP (USER_ID, COMP_CODE, CIF_NO, NEW_STATUS) select #{userId}, CIF.COMP_CODE, CIF.CIF_NO, '0' FROM CIF WHERE COMP_CODE = #{compCode} AND BLACK_LISTED = 1 AND CIF.STATUS IN ('A', 'S', 'C', 'I') AND EXISTS (SELECT 1 FROM CIF_BLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.BL_CODE = #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE M.COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM MOSBLACKLIST MOS WHERE MOS.COMP_CODE = #{compCode} AND MOS.CIF = CIF.CIF_NO AND MOS.STATUS = 'P' AND MOS.CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_BLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.BL_CODE != #{blackListCode})
;
UPDATE CIF SET BLACK_LISTED = 0 WHERE COMP_CODE = #{compCode} AND BLACK_LISTED = 1 AND CIF.STATUS IN ('A', 'S', 'C', 'I') AND EXISTS (SELECT 1 FROM CIF_BLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.BL_CODE = #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_MOSBLACKLIST M WHERE M.COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.MOS_CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM MOSBLACKLIST MOS WHERE MOS.COMP_CODE = #{compCode} AND MOS.CIF = CIF.CIF_NO AND MOS.STATUS = 'P' AND MOS.CODE != #{blackListCode}) AND NOT EXISTS (SELECT 1 FROM CIF_BLACKLIST M WHERE COMP_CODE = #{compCode} AND M.CIF_NO = CIF.CIF_NO AND M.BL_CODE != #{blackListCode})
;
INSERT INTO CIF_MOSBLACKLIST_UPDATES_TEMP (USER_ID, COMP_CODE, CIF_NO, NEW_STATUS) select #{userId}, CIF.COMP_CODE, CIF.CIF_NO, '1' FROM CIF WHERE CIF.BLACK_LISTED = 0 AND CIF.COMP_CODE = #{compCode} AND CIF.CIF_NO != 0 AND CIF.CIF_NO IS NOT NULL AND (CIF.CIF_NO IN (SELECT CIF_JOINT_DET.JOINT_CIF_NO FROM CIF_JOINT_DET WHERE CIF_JOINT_DET.CIF_NO = #{cifCode} AND CIF_JOINT_DET.COMP_CODE = #{compCode}) OR CIF.CIF_NO IN (SELECT CIF_JOINT_DET.CIF_NO FROM CIF_JOINT_DET WHERE CIF_JOINT_DET.JOINT_CIF_NO =#{cifCode} AND CIF_JOINT_DET.COMP_CODE = #{compCode}))
;
UPDATE CIF SET CIF.BLACK_LISTED = 1 WHERE CIF.BLACK_LISTED = 0 AND CIF.COMP_CODE = #{compCode} AND CIF.CIF_NO != 0 AND CIF.CIF_NO IS NOT NULL AND (CIF.CIF_NO IN (SELECT CIF_JOINT_DET.JOINT_CIF_NO FROM CIF_JOINT_DET WHERE CIF_JOINT_DET.CIF_NO = #{cifCode} AND CIF_JOINT_DET.COMP_CODE = #{compCode}) OR CIF.CIF_NO IN (SELECT CIF_JOINT_DET.CIF_NO FROM CIF_JOINT_DET WHERE CIF_JOINT_DET.JOINT_CIF_NO =#{cifCode} AND CIF_JOINT_DET.COMP_CODE = #{compCode}))
; end;

mapper.sql:
SELECT COUNT(1) 
FROM 	CIF
WHERE 	COMP_CODE  	= #{compCode}
AND		CIF_NO	 		<>  #{cif_no}
AND		JOIN_ID_NO 		= #{id_no, jdbcType=VARCHAR}
AND		JOIN_ID_TYPE IN 
	( 	
		SELECT 	CODE
		FROM 	ID_TYPES
		WHERE 	COMP_CODE = #{compCode}
		AND		TYPES	  = '1'			
	)

SELECT  	MAX(CIF_NO) CIF_NO,
			COUNT(1) CIF_COUNT
FROM  		CIF 
WHERE 		CIF.COMP_CODE 	= #{compCode}
AND		( 	CIF.CIF_NO  	 >=  #{cif_no} + 1  OR  CIF.CIF_NO  <=  #{cif_no} - 1 )
AND	 		CIF.TYPE  		= #{cifType, jdbcType=VARCHAR}
AND	 		CIF.ID_TYPE		= CASE WHEN #{idTypeCode} = 0 THEN CIF.ID_TYPE ELSE #{idTypeCode} END
AND	 		CIF.ID_NO  		= #{id_no, jdbcType=VARCHAR}
AND    		CIF.STATUS		 <>  'D'

	   FROM CIF LEFT OUTER JOIN CIF_EXTENDED ON  CIF.COMP_CODE= CIF_EXTENDED.COMP_CODE AND CIF.CIF_NO = CIF_EXTENDED.CIF_NO
	   		,RIFCTT
	  WHERE CIF.COMP_CODE = #{comp_code}
		AND CIF.COMP_CODE = RIFCTT.COMP_CODE
		AND CIF.CIF_TYPE = RIFCTT.TYPE_CODE
		
		AND CIF.STATUS = #{status}
    	
		
		AND RIFCTT.CLIENT = #{client}
    	
		
      	  AND CIF.CIF_NO = #{cif_no}
    	
		
    	 AND EXISTS(SELECT 1
    	 			FROM CTS_AVA_FILE,CTS_AVA_CATEG
    	 			WHERE CTS_AVA_FILE.COMP_CODE = #{comp_code}
    	 			AND CTS_AVA_FILE.COMP_CODE=CTS_AVA_CATEG.COMP_CODE
                    AND CTS_AVA_FILE.CATEGORY=CTS_AVA_CATEG.CODE
                    AND CTS_AVA_CATEG.ADD_GROUP = 'AVA'    	 			 
    	 			AND CTS_AVA_FILE.CIF_NO = CIF.CIF_NO
    	 			AND CTS_AVA_FILE.STATUS = 'A' )

