    )

mapper.sql:
SELECT * FROM ( SELECT CIF.CIF_NO CIF_NO, CIF.SHORT_NAME_ENG SHORT_NAME_ENG, CIF.LONG_NAME_ENG LONG_NAME_ENG, CIF_EXTENDED.FULL_NAME_ENG FULL_NAME_ENG, CIF.TEL TEL, CIF.RESIDENT RESIDENT, CIF.ID_NO ID_NO, CIF.CIF_TYPE CIF_TYPE, CIF.ADDITIONAL_REFERENCE ADDITIONAL_REFERENCE, CIF.CARD_NAME CARD_NAME, CIF.NICK_NAME NICK_NAME FROM CIF LEFT OUTER JOIN CIF_EXTENDED ON CIF.COMP_CODE= CIF_EXTENDED.COMP_CODE AND CIF.CIF_NO = CIF_EXTENDED.CIF_NO WHERE CIF.COMP_CODE = #{compCode ,jdbcType=NUMERIC} AND CIF.ID_NO = #{id_no ,jdbcType=NUMERIC}
UNION SELECT CIF_CONTACT.CIF_CODE CIF_NO, CIF.SHORT_NAME_ENG SHORT_NAME_ENG, CIF.LONG_NAME_ENG LONG_NAME_ENG, CIF_EXTENDED.FULL_NAME_ENG FULL_NAME_ENG, CIF.TEL TEL, CIF.RESIDENT RESIDENT, CIF.ID_NO ID_NO, CIF.CIF_TYPE CIF_TYPE, CIF.ADDITIONAL_REFERENCE ADDITIONAL_REFERENCE, CIF.CARD_NAME CARD_NAME, CIF.NICK_NAME NICK_NAME FROM CIF_CONTACT ,CIF LEFT OUTER JOIN CIF_EXTENDED ON CIF.COMP_CODE= CIF_EXTENDED.COMP_CODE AND CIF.CIF_NO = CIF_EXTENDED.CIF_NO WHERE CIF_CONTACT.COMP_CODE = CIF.COMP_CODE AND CIF_CONTACT.CIF_CODE = CIF.CIF_NO AND CIF_CONTACT.COMP_CODE = #{compCode ,jdbcType=NUMERIC} AND CIF_CONTACT.ID_NO = #{id_no ,jdbcType=NUMERIC} UNION SELECT CIF_JOINT_DET.CIF_NO CIF_NO, CIF.SHORT_NAME_ENG SHORT_NAME_ENG, CIF.LONG_NAME_ENG LONG_NAME_ENG, CIF_EXTENDED.FULL_NAME_ENG FULL_NAME_ENG, CIF.TEL TEL, CIF.RESIDENT RESIDENT, CIF.ID_NO ID_NO, CIF.CIF_TYPE CIF_TYPE, CIF.ADDITIONAL_REFERENCE ADDITIONAL_REFERENCE, CIF.CARD_NAME CARD_NAME, CIF.NICK_NAME NICK_NAME FROM CIF_JOINT_DET ,CIF LEFT OUTER JOIN CIF_EXTENDED ON CIF.COMP_CODE= CIF_EXTENDED.COMP_CODE AND CIF.CIF_NO = CIF_EXTENDED.CIF_NO WHERE CIF_JOINT_DET.COMP_CODE = CIF.COMP_CODE AND CIF_JOINT_DET.CIF_NO = CIF.CIF_NO AND CIF_JOINT_DET.COMP_CODE = #{compCode ,jdbcType=NUMERIC} AND CIF_JOINT_DET.ID_NO = #{id_no ,jdbcType=NUMERIC}
) TBL
WHERE TBL.CIF_NO = #{cif_no}

mapper.select:
SELECT MAX(CIF_NO)
FROM CIF
WHERE COMP_CODE = #{compCode}
AND CIF_NO BETWEEN #{code} AND #{cif_no}

mapper.sql:
SELECT	CIF CIF_NO
FROM	CIF_SEGEMENTATION_FINAL_TMP
WHERE 	SEGMENT_CODE = #{segmentCode}
AND 	USERID 		 = #{userId}

SELECT COMP_CODE, NEW_BRANCH_CODE BRANCH_CODE, CURRENCY_CODE, GL_CODE, CIF_SUB_NO, SL_NO, (CASE WHEN ACC_CHEQBOOKS='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_CHEQBOOKS, (CASE WHEN ACC_CARDS='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_CARDS, (CASE WHEN ACC_SAFEBOX='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_SAFEBOX, (CASE WHEN ACC_CSM_HOF='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_CSM_HOF, (CASE WHEN ACC_IISFMS_HOF='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_IISFMS_HOF, (CASE WHEN ACC_SO='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_SO, (CASE WHEN ACC_CTSSPCOND='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_CTSSPCOND, (CASE WHEN ACC_COLLATERALS='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_COLLATERALS, (CASE WHEN ACC_DEALS='Y' THEN (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Transferred' ELSE 'Transfrs' END) ELSE (CASE WHEN #{preferredLanguage} = 'EN' THEN 'Not Available' ELSE 'Non Existants' END) END) AS ACC_DEALS
FROM T_TMP_CHECK_CLOSE_ACC_SPEC TMPACC
WHERE TMPACC.CIF_SUB_NO = #{cif_no,jdbcType=NUMERIC} AND TMPACC.NEW_BRANCH_CODE = #{switchBranchCode,jdbcType=NUMERIC}

SELECT CIF.BRANCH_CODE, CIF.CIF_NO, SHORT_NAME_ENG, CIF.LONG_NAME_ENG, CIF.ADDITIONAL_REFERENCE, CIF_TYPE, CIF.TEL, CIF.RESIDENT, CIF.ID_NO, CIF.PC_IND, (SELECT CASE WHEN #{preferredLanguage} = 'L' THEN BRIEF_DESC_ENG ELSE BRIEF_DESC_ARAB END FROM RIFCTT WHERE COMP_CODE = #{compCode} AND TYPE_CODE = CIF.CIF_TYPE) TYPE_DESC, PRIORITY_CODE, (SELECT CASE WHEN #{preferredLanguage} = 'L' THEN BRIEF_DESC_ENG ELSE BRIEF_DESC_ARAB END FROM RIFPCT WHERE RIFPCT.COMP_CODE = #{compCode} AND RIFPCT.PRIORITY_CODE = CIF.PRIORITY_CODE) PRIORITY_DESC FROM CIF WHERE COMP_CODE = #{compCode}
AND CIF_NO = #{cifCode}
AND CIF.STATUS = 'A'
