mapper.select:
select 

from AMF
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF_SUB_NO = #{CIF_SUB_NO}
  and COMP_CODE = #{COMP_CODE}
  and CURRENCY_CODE = #{CURRENCY_CODE}
  and GL_CODE = #{GL_CODE}
  and SL_NO = #{SL_NO}

mapper.delete:
delete from AMF
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF_SUB_NO = #{CIF_SUB_NO}
  and COMP_CODE = #{COMP_CODE}
  and CURRENCY_CODE = #{CURRENCY_CODE}
  and GL_CODE = #{GL_CODE}
  and SL_NO = #{SL_NO}
