mapper.select:
select 

from CIF_KYC_FINANCIAL_INFO
where CIF_NO = #{CIF_NO}
  and COMP_CODE = #{COMP_CODE}
  and KYC_FINANCIAL_TYPE = #{KYC_FINANCIAL_TYPE}
  and LINE_NO = #{LINE_NO}

mapper.delete:
delete from CIF_KYC_FINANCIAL_INFO
where CIF_NO = #{CIF_NO}
  and COMP_CODE = #{COMP_CODE}
  and KYC_FINANCIAL_TYPE = #{KYC_FINANCIAL_TYPE}
  and LINE_NO = #{LINE_NO}
