mapper.select:
select 

from CTSCARDS_MGT
where APPLICATION_ID = #{APPLICATION_ID}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}

mapper.delete:
delete from CTSCARDS_MGT
where APPLICATION_ID = #{APPLICATION_ID}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
