mapper.select:
select 

from CTSTRS_BREAK_DEP
where BRANCH_CODE = #{BRANCH_CODE}
  and CB_IND = #{CB_IND}
  and COMP_CODE = #{COMP_CODE}
  and TRS_NO = #{TRS_NO}
  and TRS_TYPE = #{TRS_TYPE}

mapper.delete:
delete from CTSTRS_BREAK_DEP
where BRANCH_CODE = #{BRANCH_CODE}
  and CB_IND = #{CB_IND}
  and COMP_CODE = #{COMP_CODE}
  and TRS_NO = #{TRS_NO}
  and TRS_TYPE = #{TRS_TYPE}
