mapper.select:
select 

from CTSTRS_UPLOAD_TMP
where BATCH_NO = #{BATCH_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SERIAL_NO = #{SERIAL_NO}
  and USER_ID = #{USER_ID}

mapper.delete:
delete from CTSTRS_UPLOAD_TMP
where BATCH_NO = #{BATCH_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SERIAL_NO = #{SERIAL_NO}
  and USER_ID = #{USER_ID}
