mapper.select:
select 

from CTS_CHARGES_UNIFIED
where BATCH_NO = #{BATCH_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and DB_SESSION_ID = #{DB_SESSION_ID}
  and LINE_NO = #{LINE_NO}
  and SERIAL_NO = #{SERIAL_NO}
  and USER_ID = #{USER_ID}

mapper.delete:
delete from CTS_CHARGES_UNIFIED
where BATCH_NO = #{BATCH_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and DB_SESSION_ID = #{DB_SESSION_ID}
  and LINE_NO = #{LINE_NO}
  and SERIAL_NO = #{SERIAL_NO}
  and USER_ID = #{USER_ID}
