mapper.select:
select 

from FASAST
where CATEGORY_CODE = #{CATEGORY_CODE}
  and CLASS_CODE = #{CLASS_CODE}
  and CODE = #{CODE}
  and COMP_CODE = #{COMP_CODE}

mapper.delete:
delete from FASAST
where CATEGORY_CODE = #{CATEGORY_CODE}
  and CLASS_CODE = #{CLASS_CODE}
  and CODE = #{CODE}
  and COMP_CODE = #{COMP_CODE}
