mapper.select:
select 

,

from FMSFACILITYDET
where APP_TYPE = #{APP_TYPE}
  and BRANCH = #{BRANCH}
  and CODE = #{CODE}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}

mapper.delete:
delete from FMSFACILITYDET
where APP_TYPE = #{APP_TYPE}
  and BRANCH = #{BRANCH}
  and CODE = #{CODE}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}
