mapper.select:
select 

from FMSPAYPLAN_JOINT_CIF
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and JOINT_LINE_NBR = #{JOINT_LINE_NBR}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}

mapper.delete:
delete from FMSPAYPLAN_JOINT_CIF
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and JOINT_LINE_NBR = #{JOINT_LINE_NBR}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}
