mapper.select:
select 

from FMS_CREDIT_TMP
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF_NO = #{CIF_NO}
  and COMP_CODE = #{COMP_CODE}
  and FACILITY_NO = #{FACILITY_NO}
  and LINE_NBR = #{LINE_NBR}
  and USER_ID = #{USER_ID}

mapper.delete:
delete from FMS_CREDIT_TMP
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF_NO = #{CIF_NO}
  and COMP_CODE = #{COMP_CODE}
  and FACILITY_NO = #{FACILITY_NO}
  and LINE_NBR = #{LINE_NBR}
  and USER_ID = #{USER_ID}
