mapper.select:
select 

from PFTAMFMTHPAR
where ACC_CLASS = #{ACC_CLASS}
  and BRANCH_CODE = #{BRANCH_CODE}
  and CIF_SUB_NO = #{CIF_SUB_NO}
  and CIF_TYPE = #{CIF_TYPE}
  and COMP_CODE = #{COMP_CODE}
  and CURRENCY_CODE = #{CURRENCY_CODE}
  and FACTOR_BELOW = #{FACTOR_BELOW}
  and GL_CODE = #{GL_CODE}
  and LOG_NUMBER = #{LOG_NUMBER}
  and MODARIB_RATE = #{MODARIB_RATE}
  and OTHER_PROVISION = #{OTHER_PROVISION}
  and RISK_PROV = #{RISK_PROV}
  and SL_NO = #{SL_NO}

mapper.delete:
delete from PFTAMFMTHPAR
where ACC_CLASS = #{ACC_CLASS}
  and BRANCH_CODE = #{BRANCH_CODE}
  and CIF_SUB_NO = #{CIF_SUB_NO}
  and CIF_TYPE = #{CIF_TYPE}
  and COMP_CODE = #{COMP_CODE}
  and CURRENCY_CODE = #{CURRENCY_CODE}
  and FACTOR_BELOW = #{FACTOR_BELOW}
  and GL_CODE = #{GL_CODE}
  and LOG_NUMBER = #{LOG_NUMBER}
  and MODARIB_RATE = #{MODARIB_RATE}
  and OTHER_PROVISION = #{OTHER_PROVISION}
  and RISK_PROV = #{RISK_PROV}
  and SL_NO = #{SL_NO}
