mapper.select:
select 

from PMSORDER
where BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and TRX_LINE_NBR = #{TRX_LINE_NBR}
  and TRX_NBR = #{TRX_NBR}

mapper.delete:
delete from PMSORDER
where BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and TRX_LINE_NBR = #{TRX_LINE_NBR}
  and TRX_NBR = #{TRX_NBR}
