mapper.select:
select 

from PMSPORTFOLIO_POSITION_TEMP
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and PAGE_BREAK = #{PAGE_BREAK}
  and PORTFOLIO_CIF = #{PORTFOLIO_CIF}
  and PORTFOLIO_SEQ = #{PORTFOLIO_SEQ}
  and ROW_NUMBER = #{ROW_NUMBER}
  and SECURITY_CODE1 = #{SECURITY_CODE1}
  and SECURITY_CODE2 = #{SECURITY_CODE2}
  and SHORT_POSITION = #{SHORT_POSITION}
  and SORT_ORDER = #{SORT_ORDER}
  and TRADE_DATE = #{TRADE_DATE}
  and USER_ID = #{USER_ID}

mapper.delete:
delete from PMSPORTFOLIO_POSITION_TEMP
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and PAGE_BREAK = #{PAGE_BREAK}
  and PORTFOLIO_CIF = #{PORTFOLIO_CIF}
  and PORTFOLIO_SEQ = #{PORTFOLIO_SEQ}
  and ROW_NUMBER = #{ROW_NUMBER}
  and SECURITY_CODE1 = #{SECURITY_CODE1}
  and SECURITY_CODE2 = #{SECURITY_CODE2}
  and SHORT_POSITION = #{SHORT_POSITION}
  and SORT_ORDER = #{SORT_ORDER}
  and TRADE_DATE = #{TRADE_DATE}
  and USER_ID = #{USER_ID}
