mapper.select:
select 

from PMSTRADEDET
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}
  and TRADE_SERIAL_NBR = #{TRADE_SERIAL_NBR}

mapper.delete:
delete from PMSTRADEDET
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}
  and TRADE_SERIAL_NBR = #{TRADE_SERIAL_NBR}
