mapper.select:
select 

from STF_SHIPMENT_REQUEST
where BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SHIPMENT_REQUEST_NO = #{SHIPMENT_REQUEST_NO}

mapper.delete:
delete from STF_SHIPMENT_REQUEST
where BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SHIPMENT_REQUEST_NO = #{SHIPMENT_REQUEST_NO}
