mapper.select:
select 

,

from TFSLCREQUEST1
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LC_NBR = #{LC_NBR}
  and LC_TYPE = #{LC_TYPE}
  and LC_YEAR = #{LC_YEAR}

mapper.delete:
delete from TFSLCREQUEST1
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LC_NBR = #{LC_NBR}
  and LC_TYPE = #{LC_TYPE}
  and LC_YEAR = #{LC_YEAR}
