mapper.select:
select 

,

from TRSCIFLIMITDET
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF = #{CIF}
  and CLASS = #{CLASS}
  and COMP_CODE = #{COMP_CODE}
  and CY = #{CY}
  and FROM_DATE = #{FROM_DATE}
  and PERIODICITY_NBR = #{PERIODICITY_NBR}
  and PERIODICITY_TYPE = #{PERIODICITY_TYPE}
  and SERIAL_NO = #{SERIAL_NO}

mapper.delete:
delete from TRSCIFLIMITDET
where BRANCH_CODE = #{BRANCH_CODE}
  and CIF = #{CIF}
  and CLASS = #{CLASS}
  and COMP_CODE = #{COMP_CODE}
  and CY = #{CY}
  and FROM_DATE = #{FROM_DATE}
  and PERIODICITY_NBR = #{PERIODICITY_NBR}
  and PERIODICITY_TYPE = #{PERIODICITY_TYPE}
  and SERIAL_NO = #{SERIAL_NO}
