mapper.select:
select 

from TRSDEAL_HISTORY
where AMENDMENT_NO = #{AMENDMENT_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SERIAL_NO = #{SERIAL_NO}

mapper.delete:
delete from TRSDEAL_HISTORY
where AMENDMENT_NO = #{AMENDMENT_NO}
  and BRANCH_CODE = #{BRANCH_CODE}
  and COMP_CODE = #{COMP_CODE}
  and SERIAL_NO = #{SERIAL_NO}
