mapper.select:
select 

from TRSPAYPLANDET
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}

mapper.delete:
delete from TRSPAYPLANDET
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and LINE_NBR = #{LINE_NBR}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}
