mapper.select:
select 

from TRSPAYPLAN
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}

mapper.delete:
delete from TRSPAYPLAN
where BRANCH = #{BRANCH}
  and COMP_CODE = #{COMP_CODE}
  and PLAN_NBR = #{PLAN_NBR}
  and PLAN_SEQ = #{PLAN_SEQ}
